  context "create gif" do
    it("should create a gif for hex") { subject.create_gif("000000").should == BLACK_GIF }
    it("shouldn't allow invalid colors") do
      lambda { subject.create_gif("bla") }.should raise_error(RuntimeError)
    end
  end
